src/Arena.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";

/**
 * @title Arena
 * @dev Core contract for managing coding matches, stakes, and rewards
 */
contract Arena is ReentrancyGuard, Pausable, Ownable {
    using SafeERC20 for IERC20;
    using ECDSA for bytes32;

    IERC20 public immutable gameToken;
    address public oracle;
    address public mempool; // Main mempool address for entry fees

    uint256 public protocolFeeBps = 250; // 2.5%
    uint256 public constant MAX_PROTOCOL_FEE_BPS = 1000; // 10% max
    uint256 public constant MATCH_TIMEOUT = 1 hours;
    uint256 public constant ENTRY_FEE = 0.025 ether; // 0.025 MON in wei

    enum MatchStatus {
        Created,
        Joined,
        InProgress,
        PendingResolution,
        Resolved,
        Cancelled,
        Refunded
    }

    enum MatchMode {
        SpeedSolve,
        Optimization,
        CTF
    }

    struct Match {
        bytes32 id;
        address creator;
        address opponent;
        MatchMode mode;
        uint256 stakeAmount;
        bytes32 challengeHash;
        MatchStatus status;
        address winner;
        uint256 createdAt;
        uint256 resolvedAt;
        uint256 timeLimit;
    }

    mapping(bytes32 => Match) public matches;
    mapping(address => uint256) public playerStakes; // Total staked by player

    uint256 public totalTreasuryFees;
    uint256 public matchCounter;

    event MatchCreated(
        bytes32 indexed matchId,
        address indexed creator,
        MatchMode mode,
        uint256 stakeAmount,
        bytes32 challengeHash
    );

    event MatchJoined(bytes32 indexed matchId, address indexed opponent);
    event MatchStarted(bytes32 indexed matchId);
    event MatchResolved(
        bytes32 indexed matchId,
        address indexed winner,
        address indexed loser,
        uint256 payout
    );
    event MatchCancelled(bytes32 indexed matchId, string reason);
    event MatchRefunded(bytes32 indexed matchId, address indexed player, uint256 amount);
    event EntryFeePaid(address indexed player, bytes32 indexed matchId, uint256 amount);

    event OracleUpdated(address indexed oldOracle, address indexed newOracle);
    event MempoolUpdated(address indexed oldMempool, address indexed newMempool);
    event ProtocolFeeUpdated(uint256 oldFee, uint256 newFee);
    event TreasuryWithdrawal(address indexed to, uint256 amount);

    modifier onlyOracle() {
        require(msg.sender == oracle, "Arena: caller is not oracle");
        _;
    }

    modifier validMatchId(bytes32 matchId) {
        require(matches[matchId].creator != address(0), "Arena: match does not exist");
        _;
    }

    constructor(address _gameToken, address _oracle, address _mempool) {
        require(_gameToken != address(0), "Arena: invalid game token");
        require(_oracle != address(0), "Arena: invalid oracle");
        require(_mempool != address(0), "Arena: invalid mempool");

        gameToken = IERC20(_gameToken);
        oracle = _oracle;
        mempool = _mempool;
    }

    /**
     * @dev Create a new match with stake
     */
    function createMatch(
        MatchMode mode,
        uint256 stakeAmount,
        bytes32 challengeHash,
        uint256 timeLimit
    ) external nonReentrant whenNotPaused returns (bytes32 matchId) {
        require(stakeAmount > 0, "Arena: stake amount must be positive");
        require(timeLimit > 0 && timeLimit <= MATCH_TIMEOUT, "Arena: invalid time limit");

        matchId = keccak256(abi.encodePacked(
            msg.sender,
            block.timestamp,
            matchCounter++
        ));

        // Transfer stake to contract
        gameToken.safeTransferFrom(msg.sender, address(this), stakeAmount);
        playerStakes[msg.sender] += stakeAmount;

        matches[matchId] = Match({
            id: matchId,
            creator: msg.sender,
            opponent: address(0),
            mode: mode,
            stakeAmount: stakeAmount,
            challengeHash: challengeHash,
            status: MatchStatus.Created,
            winner: address(0),
            createdAt: block.timestamp,
            resolvedAt: 0,
            timeLimit: timeLimit
        });

        emit MatchCreated(matchId, msg.sender, mode, stakeAmount, challengeHash);
    }

    /**
     * @dev Join an existing match
     */
    function joinMatch(bytes32 matchId) external payable nonReentrant whenNotPaused validMatchId(matchId) {
        Match storage match_ = matches[matchId];

        require(match_.status == MatchStatus.Created, "Arena: match not available");
        require(match_.creator != msg.sender, "Arena: cannot join own match");
        require(
            block.timestamp <= match_.createdAt + MATCH_TIMEOUT,
            "Arena: match expired"
        );
        require(msg.value == ENTRY_FEE, "Arena: incorrect entry fee");

        // Transfer entry fee to mempool
        (bool success, ) = mempool.call{value: msg.value}("");
        require(success, "Arena: entry fee transfer failed");

        // Transfer stake to contract
        gameToken.safeTransferFrom(msg.sender, address(this), match_.stakeAmount);
        playerStakes[msg.sender] += match_.stakeAmount;

        match_.opponent = msg.sender;
        match_.status = MatchStatus.Joined;

        emit EntryFeePaid(msg.sender, matchId, msg.value);
        emit MatchJoined(matchId, msg.sender);
        emit MatchStarted(matchId);
    }

    /**
     * @dev Resolve match by oracle
     */
    function resolveMatch(
        bytes32 matchId,
        address winner,
        bytes calldata signature
    ) external onlyOracle validMatchId(matchId) {
        Match storage match_ = matches[matchId];

        require(
            match_.status == MatchStatus.Joined || match_.status == MatchStatus.InProgress,
            "Arena: match not resolvable"
        );
        require(
            winner == match_.creator || winner == match_.opponent,
            "Arena: invalid winner"
        );

        // Verify oracle signature
        bytes32 messageHash = keccak256(abi.encodePacked(matchId, winner));
        bytes32 ethSignedMessageHash = messageHash.toEthSignedMessageHash();
        require(
            ethSignedMessageHash.recover(signature) == oracle,
            "Arena: invalid signature"
        );

        address loser = winner == match_.creator ? match_.opponent : match_.creator;
        uint256 totalPot = match_.stakeAmount * 2;
        uint256 protocolFee = (totalPot * protocolFeeBps) / 10000;
        uint256 payout = totalPot - protocolFee;

        // Update state
        match_.winner = winner;
        match_.status = MatchStatus.Resolved;
        match_.resolvedAt = block.timestamp;

        // Update player stakes
        playerStakes[match_.creator] -= match_.stakeAmount;
        playerStakes[match_.opponent] -= match_.stakeAmount;

        // Update treasury
        totalTreasuryFees += protocolFee;

        // Transfer payout to winner
        gameToken.safeTransfer(winner, payout);

        emit MatchResolved(matchId, winner, loser, payout);
    }

    /**
     * @dev Cancel match (only creator, only if not joined)
     */
    function cancelMatch(bytes32 matchId) external validMatchId(matchId) {
        Match storage match_ = matches[matchId];

        require(msg.sender == match_.creator, "Arena: only creator can cancel");
        require(match_.status == MatchStatus.Created, "Arena: match cannot be cancelled");

        match_.status = MatchStatus.Cancelled;
        playerStakes[match_.creator] -= match_.stakeAmount;

        // Refund creator's stake
        gameToken.safeTransfer(match_.creator, match_.stakeAmount);

        emit MatchCancelled(matchId, "Cancelled by creator");
    }

    /**
     * @dev Emergency refund for expired matches
     */
    function refundExpiredMatch(bytes32 matchId) external validMatchId(matchId) {
        Match storage match_ = matches[matchId];

        require(
            block.timestamp > match_.createdAt + MATCH_TIMEOUT + 1 hours,
            "Arena: match not expired for refund"
        );
        require(
            match_.status == MatchStatus.Created || 
            match_.status == MatchStatus.Joined ||
            match_.status == MatchStatus.InProgress,
            "Arena: match not refundable"
        );

        match_.status = MatchStatus.Refunded;

        // Refund both players if opponent joined
        if (match_.opponent != address(0)) {
            playerStakes[match_.creator] -= match_.stakeAmount;
            playerStakes[match_.opponent] -= match_.stakeAmount;

            gameToken.safeTransfer(match_.creator, match_.stakeAmount);
            gameToken.safeTransfer(match_.opponent, match_.stakeAmount);

            emit MatchRefunded(matchId, match_.creator, match_.stakeAmount);
            emit MatchRefunded(matchId, match_.opponent, match_.stakeAmount);
        } else {
            // Only refund creator
            playerStakes[match_.creator] -= match_.stakeAmount;
            gameToken.safeTransfer(match_.creator, match_.stakeAmount);

            emit MatchRefunded(matchId, match_.creator, match_.stakeAmount);
        }
    }

    /**
     * @dev Update oracle address
     */
    function updateOracle(address newOracle) external onlyOwner {
        require(newOracle != address(0), "Arena: invalid oracle address");

        address oldOracle = oracle;
        oracle = newOracle;

        emit OracleUpdated(oldOracle, newOracle);
    }

    /**
     * @dev Update mempool address
     */
    function updateMempool(address newMempool) external onlyOwner {
        require(newMempool != address(0), "Arena: invalid mempool address");

        address oldMempool = mempool;
        mempool = newMempool;

        emit MempoolUpdated(oldMempool, newMempool);
    }

    /**
     * @dev Update protocol fee
     */
    function updateProtocolFee(uint256 newFeeBps) external onlyOwner {
        require(newFeeBps <= MAX_PROTOCOL_FEE_BPS, "Arena: fee too high");

        uint256 oldFee = protocolFeeBps;
        protocolFeeBps = newFeeBps;

        emit ProtocolFeeUpdated(oldFee, newFeeBps);
    }

    /**
     * @dev Withdraw treasury fees
     */
    function withdrawTreasuryFees(address to) external onlyOwner {
        require(to != address(0), "Arena: invalid recipient");
        require(totalTreasuryFees > 0, "Arena: no fees to withdraw");

        uint256 amount = totalTreasuryFees;
        totalTreasuryFees = 0;

        gameToken.safeTransfer(to, amount);

        emit TreasuryWithdrawal(to, amount);
    }

    /**
     * @dev Pause contract
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev Unpause contract
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @dev Get match details
     */
    function getMatch(bytes32 matchId) external view returns (Match memory) {
        return matches[matchId];
    }

    /**
     * @dev Get entry fee amount
     */
    function getEntryFee() external pure returns (uint256) {
        return ENTRY_FEE;
    }

    /**
     * @dev Get mempool address
     */
    function getMempool() external view returns (address) {
        return mempool;
    }
}

src/Badges.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/utils/Counters.sol";

/**
 * @title Badges
 * @dev ERC1155 NFT contract for achievement badges in Monad Code Arena
 */
contract Badges is ERC1155, Ownable, Pausable {
    using Counters for Counters.Counter;

    Counters.Counter private _badgeIdTracker;

    struct BadgeInfo {
        string name;
        string description;
        string imageUri;
        uint8 rarity; // 0=Common, 1=Rare, 2=Epic, 3=Legendary
        bool exists;
        uint256 totalMinted;
        uint256 maxSupply; // 0 = unlimited
    }

    mapping(uint256 => BadgeInfo) public badges;
    mapping(address => bool) public minters;
    mapping(address => mapping(uint256 => bool)) public playerHasBadge;

    // Predefined badge IDs for common achievements
    uint256 public constant FIRST_WIN_BADGE = 1;
    uint256 public constant FIVE_WIN_STREAK_BADGE = 2;
    uint256 public constant TEN_WIN_STREAK_BADGE = 3;
    uint256 public constant HUNDRED_MATCHES_BADGE = 4;
    uint256 public constant SPEED_DEMON_BADGE = 5;
    uint256 public constant GAS_OPTIMIZER_BADGE = 6;
    uint256 public constant CTF_MASTER_BADGE = 7;

    event BadgeCreated(
        uint256 indexed badgeId,
        string name,
        uint8 rarity,
        uint256 maxSupply
    );

    event BadgeMinted(
        address indexed player,
        uint256 indexed badgeId,
        string badgeName
    );

    event MinterAdded(address indexed minter);
    event MinterRemoved(address indexed minter);

    modifier onlyMinter() {
        require(minters[msg.sender], "Badges: caller is not a minter");
        _;
    }

    modifier badgeExists(uint256 badgeId) {
        require(badges[badgeId].exists, "Badges: badge does not exist");
        _;
    }

    constructor(string memory baseUri) ERC1155(baseUri) {
        _initializeDefaultBadges();
    }

    /**
     * @dev Initialize default achievement badges
     */
    function _initializeDefaultBadges() private {
        _createBadge(
            FIRST_WIN_BADGE,
            "First Victory",
            "Congratulations on your first win!",
            "first_win.png",
            0, // Common
            0  // Unlimited
        );

        _createBadge(
            FIVE_WIN_STREAK_BADGE,
            "Five Strike",
            "Won 5 matches in a row",
            "five_streak.png",
            1, // Rare
            0  // Unlimited
        );

        _createBadge(
            TEN_WIN_STREAK_BADGE,
            "Perfect Ten",
            "Won 10 matches in a row",
            "ten_streak.png",
            2, // Epic
            0  // Unlimited
        );

        _createBadge(
            HUNDRED_MATCHES_BADGE,
            "Veteran Coder",
            "Completed 100 matches",
            "veteran.png",
            1, // Rare
            0  // Unlimited
        );

        _createBadge(
            SPEED_DEMON_BADGE,
            "Speed Demon",
            "Solved challenge in under 30 seconds",
            "speed_demon.png",
            2, // Epic
            0  // Unlimited
        );

        _createBadge(
            GAS_OPTIMIZER_BADGE,
            "Gas Optimizer",
            "Won optimization battle with 50%+ gas savings",
            "gas_optimizer.png",
            2, // Epic
            0  // Unlimited
        );

        _createBadge(
            CTF_MASTER_BADGE,
            "CTF Master",
            "Won 10 CTF matches",
            "ctf_master.png",
            3, // Legendary
            100 // Limited supply
        );

        // Set badge ID counter to continue from predefined badges
        _badgeIdTracker._value = 7;
    }

    /**
     * @dev Create a new badge type
     */
    function _createBadge(
        uint256 badgeId,
        string memory name,
        string memory description,
        string memory imageUri,
        uint8 rarity,
        uint256 maxSupply
    ) private {
        require(!badges[badgeId].exists, "Badges: badge already exists");
        require(rarity <= 3, "Badges: invalid rarity");

        badges[badgeId] = BadgeInfo({
            name: name,
            description: description,
            imageUri: imageUri,
            rarity: rarity,
            exists: true,
            totalMinted: 0,
            maxSupply: maxSupply
        });

        emit BadgeCreated(badgeId, name, rarity, maxSupply);
    }

    /**
     * @dev Create a new custom badge (only owner)
     */
    function createCustomBadge(
        string memory name,
        string memory description,
        string memory imageUri,
        uint8 rarity,
        uint256 maxSupply
    ) external onlyOwner returns (uint256 badgeId) {
        _badgeIdTracker.increment();
        badgeId = _badgeIdTracker.current();

        _createBadge(badgeId, name, description, imageUri, rarity, maxSupply);
    }

    /**
     * @dev Add minter (Arena contract, etc.)
     */
    function addMinter(address minter) external onlyOwner {
        require(minter != address(0), "Badges: invalid minter address");
        minters[minter] = true;
        emit MinterAdded(minter);
    }

    /**
     * @dev Remove minter
     */
    function removeMinter(address minter) external onlyOwner {
        minters[minter] = false;
        emit MinterRemoved(minter);
    }

    /**
     * @dev Mint badge to player (only minters)
     */
    function mintBadge(
        address player,
        uint256 badgeId
    ) external onlyMinter whenNotPaused badgeExists(badgeId) {
        require(player != address(0), "Badges: invalid player address");
        require(!playerHasBadge[player][badgeId], "Badges: player already has badge");

        BadgeInfo storage badge = badges[badgeId];

        // Check max supply
        if (badge.maxSupply > 0) {
            require(
                badge.totalMinted < badge.maxSupply,
                "Badges: max supply reached"
            );
        }

        // Mint badge (amount = 1 for achievements)
        _mint(player, badgeId, 1, "");

        // Update tracking
        playerHasBadge[player][badgeId] = true;
        badge.totalMinted++;

        emit BadgeMinted(player, badgeId, badge.name);
    }

    /**
     * @dev Batch mint multiple badges to player
     */
    function mintBadgesBatch(
        address player,
        uint256[] memory badgeIds
    ) external onlyMinter whenNotPaused {
        require(player != address(0), "Badges: invalid player address");
        require(badgeIds.length > 0, "Badges: empty badge list");

        uint256[] memory amounts = new uint256[](badgeIds.length);

        for (uint256 i = 0; i < badgeIds.length; i++) {
            uint256 badgeId = badgeIds[i];

            require(badges[badgeId].exists, "Badges: badge does not exist");
            require(!playerHasBadge[player][badgeId], "Badges: player already has badge");

            BadgeInfo storage badge = badges[badgeId];

            // Check max supply
            if (badge.maxSupply > 0) {
                require(
                    badge.totalMinted < badge.maxSupply,
                    "Badges: max supply reached"
                );
            }

            // Update tracking
            playerHasBadge[player][badgeId] = true;
            badge.totalMinted++;
            amounts[i] = 1;

            emit BadgeMinted(player, badgeId, badge.name);
        }

        // Batch mint
        _mintBatch(player, badgeIds, amounts, "");
    }

    /**
     * @dev Get badge information
     */
    function getBadgeInfo(uint256 badgeId) external view returns (BadgeInfo memory) {
        require(badges[badgeId].exists, "Badges: badge does not exist");
        return badges[badgeId];
    }

    /**
     * @dev Check if player has specific badge
     */
    function hasBadge(address player, uint256 badgeId) external view returns (bool) {
        return playerHasBadge[player][badgeId];
    }

    /**
     * @dev Get all badges owned by player
     */
    function getPlayerBadges(address player) external view returns (uint256[] memory) {
        uint256 totalBadges = _badgeIdTracker.current();
        uint256[] memory tempBadges = new uint256[](totalBadges);
        uint256 count = 0;

        for (uint256 i = 1; i <= totalBadges; i++) {
            if (playerHasBadge[player][i]) {
                tempBadges[count] = i;
                count++;
            }
        }

        // Create properly sized array
        uint256[] memory playerBadges = new uint256[](count);
        for (uint256 i = 0; i < count; i++) {
            playerBadges[i] = tempBadges[i];
        }

        return playerBadges;
    }

    /**
     * @dev Update base URI for metadata
     */
    function setURI(string memory newUri) external onlyOwner {
        _setURI(newUri);
    }

    /**
     * @dev Pause contract
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev Unpause contract
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @dev Override transfer to prevent badge trading
     */
    function safeTransferFrom(
        address from,
        address to,
        uint256 id,
        uint256 amount,
        bytes memory data
    ) public pure override {
        revert("Badges: transfers not allowed");
    }

    /**
     * @dev Override batch transfer to prevent badge trading
     */
    function safeBatchTransferFrom(
        address from,
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    ) public pure override {
        revert("Badges: transfers not allowed");
    }
}

src/GameToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/Pausable.sol";

/**
 * @title GameToken
 * @dev ERC20 token for Monad Code Arena platform
 * Native staking and reward token $GAME
 */
contract GameToken is ERC20, ERC20Burnable, Ownable, Pausable {
    uint256 private constant INITIAL_SUPPLY = 1_000_000_000 * 10**18; // 1 billion tokens
    uint256 private constant MAX_SUPPLY = 10_000_000_000 * 10**18; // 10 billion tokens max

    mapping(address => bool) public minters;

    event MinterAdded(address indexed minter);
    event MinterRemoved(address indexed minter);

    modifier onlyMinter() {
        require(minters[msg.sender], "GameToken: caller is not a minter");
        _;
    }

    constructor() ERC20("Game Token", "GAME") {
        _mint(msg.sender, INITIAL_SUPPLY);
    }

    /**
     * @dev Add a new minter (e.g., Arena contract for rewards)
     */
    function addMinter(address minter) external onlyOwner {
        require(minter != address(0), "GameToken: minter cannot be zero address");
        minters[minter] = true;
        emit MinterAdded(minter);
    }

    /**
     * @dev Remove a minter
     */
    function removeMinter(address minter) external onlyOwner {
        minters[minter] = false;
        emit MinterRemoved(minter);
    }

    /**
     * @dev Mint new tokens (only by authorized minters)
     */
    function mint(address to, uint256 amount) external onlyMinter whenNotPaused {
        require(totalSupply() + amount <= MAX_SUPPLY, "GameToken: max supply exceeded");
        _mint(to, amount);
    }

    /**
     * @dev Pause token transfers (emergency use)
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev Unpause token transfers
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @dev Override transfer to add pause functionality
     */
    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal override whenNotPaused {
        super._beforeTokenTransfer(from, to, amount);
    }
}

