// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {ReentrancyGuard} from "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/* ---------- Interfaces ---------- */

interface IGameBank {
    function transferOut(address token, address to, uint256 amount) external;
    function manager() external view returns (address);
}

interface IERC20Decimals {
    function decimals() external view returns (uint8);
}

/* ---------- Contract ---------- */

contract GameReward is Ownable, ReentrancyGuard {
    /// Diamond ERC20 token
    address public immutable diamond;
    /// GameBank vault (ต้องตั้ง manager ให้เป็นสัญญานี้)
    address public immutable bank;

    event RewardClaimed(address indexed player, uint8 indexed rank, uint256 amount);

    constructor(address owner_, address diamond_, address bank_) Ownable(owner_) {
        require(diamond_ != address(0) && bank_ != address(0), "ZeroAddress");
        diamond = diamond_;
        bank = bank_;
    }

    /// เคลมรางวัลด้วยการระบุเพียง "ลำดับ" (1, 2, 3)
    function claim(uint8 rank) external nonReentrant {
        _assertBankAuth();
        uint256 amount = _amountForRank(rank);
        IGameBank(bank).transferOut(diamond, msg.sender, amount);
        emit RewardClaimed(msg.sender, rank, amount);
    }

    /* ---------- Views / Internals ---------- */

    function amountForRank(uint8 rank) external view returns (uint256) {
        return _amountForRank(rank);
    }

    function _amountForRank(uint8 rank) internal view returns (uint256) {
        if (rank < 1 || rank > 3) revert InvalidRank();
        // ใช้ 18 decimals (ทั่วไปของ ERC20)
        uint256 unit = 10 ** 18;
        if (rank == 1) return 150 * unit;
        if (rank == 2) return 50 * unit;
        return 25 * unit; // rank == 3
    }

    function _assertBankAuth() internal view {
        // ต้องตั้งค่า GameBank.manager = address(this) ก่อนใช้งาน
        address m = IGameBank(bank).manager();
        if (m != address(this)) revert BankNotAuthorized();
    }

    /* ---------- Custom Errors ---------- */
    error InvalidRank();
    error BankNotAuthorized();
}

